class Game{constructor(t){this.word=t.toUpperCase(),this.limit=this.word.length,this.attempt=1,this.maxAttempt=8,this.attempDiv=document.querySelector(".game_bottom__clue--attemps strong"),this.input=document.querySelector(".game_middle__board--input"),this.button=document.querySelector(".game_middle__board--button"),this.next=document.querySelector(".game_top__next")}nextWord(t){var e=JSON.parse(localStorage.getItem("historic"))||[];e.push({word:this.word,attempts:this.attempt,result:t}),localStorage.setItem("historic",JSON.stringify(e)),this.attempt=this.maxAttempt,this.next.style.display="block"}validWord(){var e=this.input.value.toUpperCase();if(e.length==this.limit){if(this.attempt<=this.maxAttempt){this.attempt++,this.word===e&&this.nextWord("victory");var i=e,r=[];for(let t=0;t<this.word.length;t++)this.word[t]==e[t]&&(r[t]="correct",i.replace(this.word[t],""));for(let t=0;t<this.word.length;t++)"correct"==!r[t]&&i.sum(this.word[t])&&(r[t]="attention",i.replace(this.word[t],""));var t=document.querySelector(".game_bottom__results"),s=document.createElement("div");for(let t=0;t<this.word.length;t++){var o=document.createElement("div");o.textContent=e[t],r[t]?o.classList.add(r[t]):o.classList.add("error"),s.appendChild(o)}t.appendChild(s),this.attempDiv.innerHTML=this.maxAttempt-this.attempt}}else console.log("digite uma palavra valida")}start(){this.button.addEventListener("click",()=>{this.validWord()}),this.input.addEventListener("keypress",t=>{"Enter"===t.key&&this.validWord()})}}export default Game;